var date = new Date(value);

var formattedDate = "";
var diffInMinutes = Math.floor((Date.now() - value) / (1000 * 60));
var diffInSeconds = Math.floor(((Date.now() - value) / (1000)) % 60);

if (diffInMinutes < 1) {
  formattedDate = "Last Minute";
} else if (diffInMinutes < 60) {
  formattedDate = diffInMinutes + " minutes ago";
} else if (diffInMinutes < 1440) {
  formattedDate = Math.floor(diffInMinutes / 60) + " hours ago";
} else if (diffInMinutes < 43200) {
  formattedDate = Math.floor(diffInMinutes / 1440) + " days ago";
} else if (diffInMinutes < 525600) {
  formattedDate = Math.floor(diffInMinutes / 43200) + " months ago";
} else if (diffInMinutes < 1051200) { // 1 year (60 * 24 * 365)
  formattedDate = Math.floor(diffInMinutes / 525600) + " years ago";
} else {
  formattedDate = date.getFullYear() + "-" + (date.getMonth() + 1) + "-" + date.getDate();
}

return formattedDate

