//getting svgs object
var svgs = getSvgs()

var stateParams = ctx.stateController.getStateParams();

//filter by asset status json, has for example ["asset on", "asset off", etc]
var filterByAssetStatus = data[1].filterByAssetStatus ? JSON
    .parse(data[1].filterByAssetStatus) : []

var searchSensor = data[1].searchSensor ? data[1].searchSensor : ''

//show legend toggle
var showLegend = data[1].showLegend ? data[1].showLegend :
    false
var htmlVAL = ""
var dataItems = data.slice(2)

var map = dataItems.map(item => {
    // Extract the unit from the data
    var unit = data[2].Unit;

    // Destructure the pressure-related values from the getPressureValues function
    var {
        pressure,
        avgPressure,
        maxPressure,
        minPressure
    } = getPressureValues(item, unit);

    // Check if the device matches the search criteria
    if(searchDevice(searchSensor, item) == true) {
        return returnItems(); // If it matches, return the formatted HTML element
    } else if(!searchSensor) {
        return returnItems(); // If there's no search sensor, return the formatted HTML element
    } else {
        return null; // If it doesn't match, return null
    }

    // Function to return the formatted HTML element for the item
    function returnItems() {
        // Prepare the data attribute for the HTML element, passing the entityName to the actions
        var htmlVAL = `data-value="${item.entityName}"`;

        // Get the background color, status button color, and text color based on the item's pressure values
        var {
            bgColor,
            statusButtonColor,
            color
        } = getCardColors(item, pressure);

        // Apply filters to determine if the item should be included in the map
        if (!applyFilters(item, pressure, filterByAssetStatus, "", "")) {
            return null; // If it doesn't pass the filters, return null
        }

        // Retrieve battery level and active alarm status for the item
        var battery = item.battery;
        var alarm = item.activeAlarm;

        // Determine the active color based on the item's active status
        var activeColor = item.active === true ? 'green' : 'red';

        // Return the formatted HTML element for the item
        return `<div class="subContainer ${color}" style="background-color: ${bgColor};" id="asset_details" value="${item.entityName}" ${htmlVAL}>
            <div style="font-weight: 700; font-size: 20px;" ${htmlVAL}>${item.entityLabel}</div>
            <div class="items-center" ${htmlVAL}>
                <span class="material-icons icon" ${htmlVAL}>
                    compress
                </span>
                <div class="pressure-info" ${htmlVAL}>
                    <div ${htmlVAL}>
                        <span ${htmlVAL}>CURR</span>
                        <span class="pressure-value pressure-curr"  ${htmlVAL}>${pressure}</span>
                        <span ${htmlVAL}>${unit}</span>
                    </div>
                    <div ${htmlVAL}>
                        <span ${htmlVAL}>MIN</span>
                        <span class="pressure-value" ${htmlVAL}>${minPressure}</span>
                        <span ${htmlVAL}>${unit}</span>
                    </div>
                    <div ${htmlVAL}>
                        <span ${htmlVAL}>AVG</span>
                        <span class="pressure-value" ${htmlVAL}>${avgPressure}</span>
                        <span ${htmlVAL}>${unit}</span>
                    </div>
                    <div ${htmlVAL}>
                        <span ${htmlVAL}>MAX</span>
                        <span class="pressure-value" ${htmlVAL}>${maxPressure}</span>
                        <span ${htmlVAL}>${unit}</span>
                    </div>
                </div>
            </div>
            <div style="display: flex; justify-content: space-between;" class="info-container" ${htmlVAL}>
                <div class="items-center text" ${htmlVAL}>
                    ${getBatteryText(item.battery).icon}
                    ${getBatteryText(item.battery).text}
                </div>
                <div class="items-center text" ${htmlVAL}>
                    ${getSignalText(item.rssi).icon}  ${getSignalText(item.rssi).text}
                </div>
                <div style="color: white" class="text" ${htmlVAL}>
                    <span ${htmlVAL}>${status == true || status == 'true' ? 'Total Usage ' : 'Total Usage '} ${(formatTimeFromSeconds(item.OnTime / 1000))}</span>
                </div>
            </div>
            <div class="alarmsContainer" ${htmlVAL}>
                <div class='statusContainer' ${htmlVAL}>
                    ${alarm ? `${alarm}<span class="material-symbols-outlined" style="color: ${statusButtonColor};" id="alarm"  ${htmlVAL}>
                            warning</span>` : '<div></div>'}
                </div>
                <div>
                    <span class="material-symbols-outlined" style="cursor: pointer; color: ${statusButtonColor};" ${htmlVAL} id="device_timeline">
                        monitoring
                    </span>
                </div>
                <div class='' ${htmlVAL}>
                    ${item.asset_status_confirmed == false ? `<span class="material-symbols-outlined circle-icon" style="color: ${statusButtonColor}; " id="turn-on"  ${htmlVAL}>
                                check_circle
                            </span>` : '<div></div>'}
                    ${item.asset_status_confirmed == true ? `<span class="material-symbols-outlined circle-icon" style="color: ${statusButtonColor}; " id="turn-off"  ${htmlVAL}>
                                cancel 
                            </span>` : '<div></div>'} 
                </div>
            </div>
        </div>`;
    }
})

//function to get bg color
function getCardColors(item, pressure) {
    let bgColor = 'black';
    let statusButtonColor = '';
    let color = 'black';

    if(item.active=='false' || item.active==false) {
        // bgColor = '#E25A5A';
        bgColor = 'gray';
        statusButtonColor = "white";
        color = 'white';
    }
    else if (pressure <= 0.25) { //blue
        bgColor = '#154c79';
        statusButtonColor = "white";
        color = 'white';
    } else if (pressure <= 6) { //green
        bgColor = '#279B54';
        statusButtonColor = "white";
        color = 'white';
    } else if (pressure <= 10) { //yellow
        bgColor = '#eab676';
        statusButtonColor = "black";
        color = 'black';
    } else if(pressure>10){
        bgColor = '#FFA500';
        statusButtonColor = "white";
        color = 'white';
    }
    //asset off
    else {
        // bgColor = '#E25A5A';
        bgColor = 'gray';
        statusButtonColor = "white";
        color = 'white';
    }

    return {
        bgColor,
        statusButtonColor,
        color
    };
}

//function to apply filters

function applyFilters(item, pressure, filterByAssetStatus,
    filterAboveOrBelowPressureOption,
    filterAboveOrBelowPressureValue) {

    /*if(pressure>10){
        return false
    }*/

    //DEVICES MUST BE ACTIVE FOR PRESSURE VALIDATIONS


    // Check if the filter includes "asset on"
    if (filterByAssetStatus.includes("The pressure is below")) {
        // If the asset status is 'true' or true and the pressure is greater than or equal to 0.25, exclude the item
        if (parseFloat(pressure) <= 0.25 && (item.active === 'true' || item.active ===true)) {
            return false;
        }
    }
    // Check if the filter includes "device off"
    if (filterByAssetStatus.includes("device off")) {
        // If the device is inactive ('false' or false), exclude the item
        if (item.active === 'false' || item.active ===false) {
            return false;
        }
    }

    // Check if the filter includes "asset off reported on"
    if (filterByAssetStatus.includes("The pressure is normal")) {
        // If the asset status is 'true' or true and the pressure is less than 0.25, exclude the item
        if (parseFloat(pressure) > 0.25 && parseFloat(
                pressure) <= 6 && (item.active === 'true' || item.active ===true)) {
            return false;
        }
    }

    // Check if the filter includes "asset off"
    if (filterByAssetStatus.includes("The pressure is above")) {
        // If the asset status is 'false' or false, exclude the item
        if (parseFloat(pressure) > 6 && parseFloat(
                pressure) <= 10 
                && (item.active === 'true' || item.active ===true)) {
            return false;
        }
    }

    if (filterByAssetStatus.includes("The pressure is high")) {
        // If the asset status is 'false' or false, exclude the item
        if (parseFloat(pressure) > 10 && (item.active === 'true' || item.active ===true)) {
            return false;
        }
    }


    if (filterByAssetStatus.includes("The pressure is high")) {
        // If the asset status is 'false' or false, exclude the item
        if (parseFloat(pressure) > 10 && (item.active === 'true' || item.active ===true)) {
            return false;
        }
    }

    // If none of the conditions are met, include the item
    return true;
}


//function to get pressure values
function getPressureValues(item, unit) {
    return {
        pressure: getPressure(item.pressure, unit),
        avgPressure: item.avgPressure ? getPressure(item
            .avgPressure, unit) : 0,
        maxPressure: item.maxPressure ? getPressure(item
            .maxPressure, unit) : 0,
        minPressure: item.minPressure ? getPressure(item
            .minPressure, unit) : 0
    };
}


//convert pressure to other units depending device settings
function getPressure(value, unit) {
    if (value.toString() == "0") {
        return 0
    }

    if (value) {
        if (value.toString() == "0") {
            return 0
        }

        if (value || value.toString() == "0") {
            //mbar to mWC
            if (unit == 'mmWC') {
                if (value || value.toString() == "0")
                return (value * 10.197162129779)
                    .toFixed(1)
                return value.toFixed(1)
            }
            //mbar to inWC
            else if (unit == 'inWC') {
                if (value || value.toString() == "0")
                return (value * 0.40146307866177)
                    .toFixed(1)
                if (value || value.toString() == "0")
                return (value).toFixed(1)

                return value.toFixed(1)
            }
            return value.toFixed(1)
        }
    }
}


