//function to get the battery text: Low, Medium, High, Very High or Full
function getBatteryText(batt) {
    let result_text = "",
        icon = ""
    if (batt >= 0 && batt <= 10) {
        result_text = "Low"; // rojo
        icon = `<span class="material-icons">
            battery_1_bar
        </span>`
    } else if (batt > 10 && batt <= 30) {
        result_text = "Medium"; // naranja
        icon = `<span class="material-icons">
            battery_2_bar
        </span>`
    } else if (batt > 30 && batt <= 50) {
        result_text = "High"; // amarillo
        icon = `<span class="material-icons">
            battery_4_bar
        </span>`
    } else if (batt > 50 && batt <= 70) {
        result_text = "Very High"; // verde oscuro
        icon = `<span class="material-icons">
            battery_5_bar
        </span>`
    } else if (batt > 70 && batt <= 100) {
        result_text = "Full"; // verde claro
        icon = `<span class="material-icons">
            battery_full
        </span>`
    }
    return {
        text: result_text,
        icon
    }
}

//function to get the rssi text: Very Strong, Strong, Moderate, etc
function getSignalText(rssi) {
    let result_signal = "",
        icon = ""
    if (rssi > -70 && rssi <= -40) {
        result_text = "Very strong"
        icon = `<span class="material-icons">
            signal_wifi_statusbar_4_bar
        </span>`
    } else if (rssi > -100 && rssi <= -70) {
        result_text = "Strong" // green
        icon = `<span class="material-icons">
            signal_wifi_statusbar_4_bar
        </span>`
    } else if (rssi > -120 && rssi <= -
        100) { //low signal power
        result_text = "Moderate"
        icon = `<span class="material-icons">
            network_wifi_3_bar
        </span>`
    } else if (rssi > -120 && rssi <= -
        105) { //really low signal power (problems to make calls)
        result_text = "Weak"
        icon = `<span class="material-icons">
            network_wifi_1_bar
        </span>`
    } else if (rssi <= -120) {
        result_text = "Super weak"
        icon = `<span class="material-icons">
            network_wifi_1_bar
        </span>`
    }
    return {
        text: result_text,
        icon
    }
}


//for showing in total Usage
function formatTimeFromSeconds(seconds) {
    seconds = parseInt(seconds)

    // Constants for time conversions
    const oneMinute = 60;
    const oneHour = 60 * oneMinute;
    const oneDay = 24 * oneHour;
    const oneYear = 365 * oneDay;
    const oneMonth = 30 *
    oneDay; // Assuming an average of 30 days per month

    // Calculate individual time components
    let years = Math.floor(seconds / oneYear);
    let months = Math.floor((seconds % oneYear) / oneMonth);
    let days = Math.floor(((seconds % oneYear) % oneMonth) /
        oneDay);
    let hours = Math.floor((((seconds % oneYear) %
        oneMonth) % oneDay) / oneHour);
    let minutes = Math.floor(
        ((((seconds % oneYear) % oneMonth) % oneDay) %
            oneHour) / oneMinute
    );
    let remainingSeconds = seconds % oneMinute;

    // Initialize the result string
    let resultText = "";

    // Mostrar mÃ¡ximo dos componentes no nulos
    let componentsCount = 0;

    // Include years if greater than 0 and there is still room for more components
    if (years > 0 && componentsCount < 2) {
        resultText += `${years}y `;
        componentsCount++;
    }

    // Include months if greater than 0 and there is still room for more components
    if (months > 0 && componentsCount < 2) {
        resultText += `${months}mo `;
        componentsCount++;
    }

    // Include days if greater than 0 and there is still room for more components
    if (days > 0 && componentsCount < 2) {
        resultText += `${days}d `;
        componentsCount++;
    }

    // Include hours if greater than 0 and there is still room for more components
    else if (hours > 0 && componentsCount < 2) {
        resultText += `${hours}h `;
        componentsCount++;
    }

    // Include minutes if greater than 0 and there is still room for more components
    else if (minutes > 0 && componentsCount < 2) {
        resultText += `${minutes}min `;
        componentsCount++;
    }

    // Include remaining seconds if greater than 0 and there is still room for more components
    else if (remainingSeconds > 0 && componentsCount < 2) {
        resultText += `${remainingSeconds.toFixed(0)}s`;
        componentsCount++;
    }

    // If no components were added, default to "0s"
    if (resultText.trim() === "") {
        resultText = "0s";
    }

    // Return the trimmed result string
    return resultText.trim();
}


