function searchDevice(searchSensor, item) {
    // Check if the search term is not defined
    if (!searchSensor) return false;

    // Convert the search term to lowercase
    const searchTerm = searchSensor.toString().toLowerCase();

    // Array of fields to search within the item
    const fields = [
        'entityLabel',
        'entityName',
        'devEUI',
        'related_location',
        'related_area',
        'related_asset'
    ];

    // Check if any of the fields contain the search term
    return fields.some(field => 
        item[field] && item[field].toLowerCase().includes(searchTerm)
    );
}

