let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap
    .get("customDialog"));
let deviceService = $injector.get(
    widgetContext.servicesMap.get("deviceService")
);
let attributeService = $injector.get(
    widgetContext.servicesMap.get("attributeService")
);

var value = $event.target.getAttribute("data-value");
//var entityService = widgetContext.entityService

var stateParams = widgetContext.stateController.getStateParams()

var jwtToken = localStorage.getItem('jwt_token')

const relationsHeaders = {
	accept: "application/json",
	"Content-Type": "application/json",
	Authorization: `Bearer ${jwtToken}`,
}

const relationsHeadersContent = {
	method: "GET",
	headers: relationsHeaders
};


		fetch(
			`https://iotlogiq.com:443/api/tenant/devices?deviceName=${value}`, 
			relationsHeadersContent
		)
			.then((response) => response.json())
			.then((data) => {
			    entityId = data.id
			    
			    
                openEditDeviceDialog();
                
                function openEditDeviceDialog() {
                    customDialog
                        .customDialog(htmlTemplate,
                            EditDeviceDialogController)
                        .subscribe();
                }
			    
    			function EditDeviceDialogController(instance) {
                    let vm = instance;
			    
			        //FLAG
    			    var initial_flag = widgetContext.data.find(item => item
                        .dataKey.name ==
                        'enable_asset_status_confirmed' && item
                        .datasource.entityName == data.name).data[0][
                    1] == 'true'
            
                    initial_flag = initial_flag.toString() == 'true' ?
                        true : false
                    vm.flag = initial_flag ? initial_flag : false
                    
                    
                    //STATUS
                    vm.status = false
                    var initial_status = widgetContext.data.find(item => item
                        .dataKey.name ==
                        'asset_status_confirmed' && item
                        .datasource.entityName == data.name).data[0][
                    1] == true || widgetContext.data.find(item => item
                        .dataKey.name ==
                        'asset_status_confirmed' && item
                        .datasource.entityName == data.name).data[0][
                    1] == 'true'
            
                    /*initial_status = initial_status.toString() == 'true' ?
                        true : false*/
                    vm.status = initial_status
                
                
                    vm.device = {};
                
                    vm.editDeviceFormGroup = vm.fb.group({
                        //name: ["", [vm.validators.required]],
                        //type: ["", [vm.validators.required]],
                        asset_status_confirmed: [false],
                        enable_asset_status_confirmed: [false]
                    });
                
                    getDevice();
                
                    vm.cancel = function() {
                        vm.dialogRef.close(null);
                    };
                
                    vm.onEnableChange = function(e) {
                        vm.flag = vm.flag
                    }
                
                    vm.onChangeStatus = function(e) {
                        vm.status  = e.checked=='true' || e.checked==true ? true : false
                    }
                
                    vm.save = function() {
                        vm.editDeviceFormGroup.markAsPristine();
                        widgetContext.rxjs
                            .forkJoin([
                                saveAttributes(entityId),
                                //saveRelations(entityId),
                                saveDevice(),
                            ])
                            .subscribe(function() {
                                widgetContext.updateAliases();
                                vm.dialogRef.close(null);
                            });
                    };
                    
                    /*var initial_flag = widgetContext.data.find(item => item
                        .dataKey.name ==
                        'enable_asset_status_confirmed' && item
                        .datasource.entityName == entityName).data[0][
                    1] == 'true'
            
                    initial_flag = initial_flag.toString() == 'true' ?
                        true : false
                
                    vm.flag = initial_flag ? initial_flag : false
                    vm.status = null*/
                
                    function getDevice() {
                        deviceService.getDevice(entityId.id).subscribe(
                            function(device) {
                                vm.device = device;
                                vm.editDeviceFormGroup.patchValue({
                                    //name: vm.device.name,
                                    //type: vm.device.type,
                                    /*label: vm.device.label,
                                    notes: widgetContext.data
                                        .find(item => item
                                            .dataKey.name ==
                                            'notes' && item
                                            .datasource
                                            .entityName ==
                                            entityName).data[0][
                                            1
                                        ],*/
                                    asset_status_confirmed: widgetContext.data.find(item=>item.dataKey.name=='asset_status_confirmed' && item.datasource.entityName==entityName).data[0][1],
                                    enable_asset_status_confirmed: initial_flag
                                }, {
                                    emitEvent: false
                                });
                            });
                    }
                
                    function saveAttributes(entityId) {
                        const formValues = vm.editDeviceFormGroup.value;
                        let attributesArray = [];
                        attributesArray.push(
                        {
                            key: "asset_status_confirmed",
                            value: formValues
                                .enable_asset_status_confirmed!=false || formValues
                                .enable_asset_status_confirmed!='false' ? vm.status : ""
                        }, {
                            key: "enable_asset_status_confirmed",
                            value: formValues
                                .enable_asset_status_confirmed
                        });
                        if (attributesArray.length > 0) {
                            return attributeService.saveEntityAttributes(
                                entityId,
                                "SERVER_SCOPE",
                                attributesArray
                            );
                        }
                        return widgetContext.rxjs.of([]);
                    }
                
                    function saveDevice() {
                        const formValues = vm.editDeviceFormGroup.value;
                        //vm.device.name = formValues.name;
                        //if (vm.device.type !== formValues.type) {
                        //vm.device.type = formValues.type;
                        //vm.device.deviceProfileId = null;
                        //}
                        //vm.device.label = formValues.label;
                        return deviceService.saveDevice(vm.device);
                    }
                }
			})
			.catch((error) => console.error(error));

