function openEditDeviceDialog() {
    customDialog
        .customDialog(htmlTemplate,
            EditDeviceDialogController)
        .subscribe();
}

function EditDeviceDialogController(instance) {
    let vm = instance

    vm.device = {};

    vm.status = false

    vm.editDeviceFormGroup = vm.fb.group({
        assets_on: [[]],
        assets_off: [[]],
        flag: [false]
    });

    vm.devices = []
    vm.devices = widgetContext.datasources.slice(1)
    vm.selectedDevices = []

    vm.onChangeStatus = function(e, name) {
        vm.status  = e.checked=='true' || e.checked==true ? true : false
    }

    getDevice();

    function addDevice(device) {
        vm.editDeviceFormGroup.get('flag').setValue(true);
        if (vm.selectedDevices.indexOf(device.entityId) === -1) {
            vm.selectedDevices.push(device.entityId);
        }
    }

    function removeDevice(device) {
        const index = vm.selectedDevices.indexOf(device.entityId);

        if (index !== -1) {
            vm.selectedDevices.splice(index, 1);
        }
    }

    vm.onCheckboxChange = function(event, device) {
        if (event.checked) {
            addDevice(device);
        } else {
            removeDevice(device);
        }
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.onEnableChange = function(e) {
        vm.flag = vm.flag
    }

    vm.onChangeStatus = function(e) {
        vm.status  = e.checked=='true' || e.checked==true ? true : false
    }

    vm.save = function() {
        vm.editDeviceFormGroup.markAsPristine();
        widgetContext.updateAliases();
        vm.dialogRef.close(null);
